class RinaAsleep : public AnimationProfile {

	public:
		
		void renderFrame (const size_t &frameNumber, const CRGB &animationColor) {

			if (frameNumber > 170) {
				renderCanvas(this->canvas, RINA_ASLEEP, RINA_ASLEEP_LENGTH);
				return;
			}

			if (frameNumber > 160) {
				renderCanvas(this->canvas, RINA_ASLEEP_DROOL7, RINA_ASLEEP_DROOL7_LENGTH);
				return;
			}
			
			if (frameNumber > 150) {
				renderCanvas(this->canvas, RINA_ASLEEP_DROOL6, RINA_ASLEEP_DROOL6_LENGTH);
				return;
			}
			
			if (frameNumber > 140) {
				renderCanvas(this->canvas, RINA_ASLEEP_DROOL5, RINA_ASLEEP_DROOL5_LENGTH);
				return;
			}
			
			if (frameNumber > 128) {
				renderCanvas(this->canvas, RINA_ASLEEP_DROOL4, RINA_ASLEEP_DROOL4_LENGTH);
				return;
			}
			
			if (frameNumber > 32*3) {
				renderCanvas(this->canvas, RINA_ASLEEP_DROOL3, RINA_ASLEEP_DROOL3_LENGTH);
				return;
			}
			
			if (frameNumber > 32*2) {
				renderCanvas(this->canvas, RINA_ASLEEP_DROOL2, RINA_ASLEEP_DROOL2_LENGTH);
				return;
			}

			if (frameNumber > 32) {
				renderCanvas(this->canvas, RINA_ASLEEP_DROOL1, RINA_ASLEEP_DROOL1_LENGTH);
				return;
			}

			renderCanvas(this->canvas, RINA_ASLEEP, RINA_ASLEEP_LENGTH);
		}

		RinaAsleep (const CRGBArray<NUM_LEDS> &canvas): AnimationProfile(canvas) {}

};